package dmitriy.blanutsa.coursework.graph;import com.coxautodev.graphql.tools.GraphQLQueryResolver;import com.coxautodev.graphql.tools.GraphQLResolver;import dmitriy.blanutsa.coursework.persistance.entities.Manufacturer;import dmitriy.blanutsa.coursework.persistance.repositories.ManufacturerRepository;import org.springframework.beans.factory.annotation.Autowired;import org.springframework.stereotype.Component;import java.util.List;@Componentpublic class ManufacturerQueries implements GraphQLQueryResolver {    private final ManufacturerRepository manufacturerRepository;    @Autowired    public ManufacturerQueries(ManufacturerRepository manufacturerRepository) {        this.manufacturerRepository = manufacturerRepository;    }    private List<Manufacturer> getList() {        return manufacturerRepository.findAll();    }}