package dmitriy.blanutsa.coursework.persistance.entities;import lombok.Getter;import lombok.Setter;import javax.persistence.*;import javax.validation.constraints.NotNull;import java.util.Date;@Getter@Setter@Entity@Table(name = "attorney_power")public class AttorneyPower extends BaseEntity {    @ManyToOne(fetch = FetchType.LAZY)    @JoinColumn(name = "person_id", nullable = false, insertable = false, updatable = false)    private Person person;    @ManyToOne(fetch = FetchType.LAZY)    @JoinColumn(name = "car_number", nullable = false, insertable = false, updatable = false)    private Car car;    @Temporal(TemporalType.DATE)    @Column(name = "from_date", nullable = false)    private Date fromDate;    @Temporal(TemporalType.DATE)    @Column(name = "to_date", nullable = false)    private Date toDate;}