package dmitriy.blanutsa.coursework.persistance.entities;import lombok.Getter;import lombok.Setter;import org.hibernate.annotations.CreationTimestamp;import org.hibernate.annotations.UpdateTimestamp;import org.springframework.data.annotation.CreatedBy;import javax.persistence.*;import java.util.Date;@Getter@Setter@MappedSuperclasspublic abstract class BaseEntity {    @Id    @GeneratedValue    @Column(name = "id")    private Integer id;    @CreationTimestamp    @Temporal(TemporalType.TIMESTAMP)    @Column(name = "created_at")    private Date createdAt;    @UpdateTimestamp    @Temporal(TemporalType.TIMESTAMP)    @Column(name = "updated_at")    private Date updatedAt;}