package dmitriy.blanutsa.coursework.persistance.entities;import lombok.Getter;import lombok.Setter;import org.hibernate.annotations.CreationTimestamp;import org.hibernate.annotations.UpdateTimestamp;import javax.persistence.*;import java.util.Date;@Getter@Setter@MappedSuperclasspublic abstract class BaseEntityWithoutId {    @CreationTimestamp    @Temporal(TemporalType.TIMESTAMP)    @Column(name = "created_at")    private Date createdAt;    @UpdateTimestamp    @Temporal(TemporalType.TIMESTAMP)    @Column(name = "updated_at")    private Date updatedAt;}