package dmitriy.blanutsa.coursework.persistance.entities;import lombok.Getter;import lombok.Setter;import org.hibernate.validator.constraints.Length;import javax.persistence.*;import javax.validation.constraints.NotBlank;import javax.validation.constraints.NotNull;import javax.validation.constraints.Past;import java.util.Date;import java.util.List;@Getter@Setter@Entity@Table(name = "car")public class Car extends BaseEntityWithoutId {    @Id    @Column(name = "number", length = 50)    private String number;    @ManyToOne(fetch = FetchType.LAZY)    @JoinColumn(name = "model_id", nullable = false, insertable = false, updatable = false)    private Model model;    @Column(name = "model_id", nullable = false)    private Integer modelId;    @Temporal(TemporalType.DATE)    @Column(name = "product_year", nullable = false)    private Date productYear;    @OneToMany(fetch = FetchType.LAZY)    @JoinColumn(name = "car_number")    private List<Picture> pictures;    /*@OneToOne(fetch = FetchType.LAZY)    @JoinColumn(name = "owner_id", nullable = false, insertable = false, updatable = false)    private Person owner;*/    @OneToMany(fetch = FetchType.LAZY)    @JoinColumn(name = "person_id")    private List<AttorneyPower> attorneyPowers;}