package dmitriy.blanutsa.coursework.persistance.entities;import lombok.Getter;import lombok.Setter;import javax.persistence.*;import javax.validation.constraints.NotNull;import java.util.List;@Getter@Setter@Entity@Table(name = "manufacturer")public class Manufacturer extends BaseEntity {    @Column(name = "name", length = 50, nullable = false)    private String name;    @Column(name = "description", length = 50, nullable = false)    private String description;    @OneToMany(fetch = FetchType.LAZY)    @JoinColumn(name = "manufacturer_id")    private List<Model> models;}