package dmitriy.blanutsa.coursework.persistance.entities;import lombok.Getter;import lombok.Setter;import javax.persistence.*;@Getter@Setter@Entity@Table(name = "model")public class Model extends BaseEntity {    @Column(name = "name", length = 50, nullable = false)    private String name;    @Column(name = "description", length = 50, nullable = false)    private String description;    @ManyToOne(fetch = FetchType.LAZY)    @JoinColumn(name = "manufacturer_id", nullable = false, insertable = false, updatable = false)    private Manufacturer manufacturer;    @Column(name = "manufacturer_id", nullable = false)    private Integer manufacturerId;}