package dmitriy.blanutsa.coursework.persistance.entities;import lombok.Getter;import lombok.Setter;import javax.persistence.*;import java.util.Date;import java.util.List;@Getter@Setter@Entity@Table(name = "person")public class Person extends User {    @OneToMany(fetch = FetchType.LAZY)    @JoinColumn(name = "person_id")    private List<AttorneyPower> attorneyPowers;    @OneToOne(fetch = FetchType.LAZY)    @JoinColumn(name = "owner_id")    private Car car;}